const $=selector=>document.querySelector(selector);$("#form").addEventListener("submit",e=>{e.preventDefault();const marks=parseInt($("#number").value);isValidate(marks)?putInDOM(marks):putInDOM("Invalid Input")});const isValidate=mark=>mark>=0&&mark<=100,putInDOM=value=>{if("number"==typeof value){const grade=getGrade(value);$(".result").innerHTML=`<h3>Your grade is ${grade}</h3>`}else $(".result").innerHTML=`<h3>${value}. Provide a number between 0-100</h3>`},getGrade=number=>number>=80?"A+":number>=70?"A":number>=60?"A-":number>=50?"B":number>=40?"C":number>=33?"D":"F";